def dotify(text):
        # حذف تمام کاراکترهای غیر الفبایی به جز فاصله‌ها
        text = text.replace('.', ' ')
        text = re.sub(r'[^a-zA-Z0-9\s]', '', text)
        slug = text.replace(' ', '.')
        return slug

