def find_rar_files(directory_path):
        try:
                rar_files = []
                # لیست فایل‌ها در مسیر داده شده
                for file in os.listdir(directory_path):
                        QtWidgets.QApplication.processEvents()
                        # بررسی اینکه آیتم مورد نظر فایل با پسوند .rar باشد
                        if file.endswith('.rar'):
                                # ساختار مسیر فایل
                                rar_path = os.path.basename(directory_path) + '/' + os.path.basename(file)
                                rar_files.append([rar_path, os.path.basename(directory_path)])
                return rar_files
        except Exception as e:
                print(f"An error occurred: {e}")
                return []


def folder_size(path='.'):
        QtWidgets.QApplication.processEvents()
        folder_size_cmd = "powershell (gci -Path '"+str(path)+"' -Recurse| Measure-Object -Property Length -s).sum"
        folder_size_output=subprocess.check_output(folder_size_cmd)
        return convert_bytes(int(folder_size_output.decode('utf-8')))

